      goodsUrl = $.petInfo.goodsInfo && $.petInfo.goodsInfo.goodsUrl;
      // option['media-url'] = goodsUrl;
      // console.log(`初始化萌宠信息完成: ${JSON.stringify(petInfo)}`);
