getArrRandomly = (arr) => {
  var len = arr.length;
  for (var i = len - 1; i >= 0; i--) {
    var randomIndex = Math.floor(Math.random() * (i + 1));
    var itemIndex = arr[randomIndex];
    arr[randomIndex] = arr[i];
    arr[i] = itemIndex;
  }
  return arr;
}


getRandomArr = (arr=[],num) => {
  const tmpArr = getArrRandomly(arr);
  let arrList = [];
  for (let i = 0; i < num; i++) {
    arrList.push(tmpArr[i]);
  };
  return arrList;
}


